INSERT INTO `customer_support` (`customer_support_id`, `customer_support_topic_id`, `customer_support_status`, `store_id`, `customer_id`, `reference`, `subject`, `enquiry`, `date_added`, `date_updated`, `customer_support_1st_category_id`, `customer_support_2nd_category_id`, `status`) VALUES
(1, 1, 'Открытый', 0, 14, 'F70AC98', 'помогите определиться с выбором', 'помогите определиться с выбором, выбираю между моделями Робот-пылесос Clever&amp;Clean 112 и Робот-пылесос Agait EC-02?\r\nнасколько будет чувствоваться разница:у первого одна угловая щетка,а у agait две угловых щетки?\r\nкакой пылесос мощнее?\r\nкакой надежнее?\r\nесть ли у них нера-фильтр?\r\n\r\nили может что-то другое?около 10т.р\r\n\r\n\r\n ', '2012-12-02 18:37:31', '2012-12-02 22:36:26', 0, 0, 1),
(2, 1, 'Открытый', 0, 0, 'F70AC98', 'RE: помогите определиться с выбором', 'Здравствуйте, Александра!\r\nСпасибо за обращение. Отвечаю на Ваши вопросы. \r\n1. боковая щетка служит для сбора пыли вдоль плинтуса и одной щетки вполне достаточно.\r\n2. мощность пылесосов примерно одинковая (порядка 40 Вт), однако время работы Clever&amp;Clean от аккумулятора чуть больше: 100 минут против 90 у Agait.\r\n3. оба пылесосы надежны, но большой популярностью пользуется Clever&amp;Clean.\r\n4. HEPA фильтр есть только у Clever&amp;Clean.\r\n\r\nРекомендую Вам купить Clever&amp;Clean 112. Есть более дорогое решение от iRobot - iRobot Roomba 620 (12 900 рублей), но принципиальных различий между ним и Clever&amp;Clean нет (цена выше из-за бренда), поэтому рекомендую  Clever&amp;Clean 112.\r\n\r\nЕсли у Вас остались вопросы - спрашивайте, с удовольствием отвечу.\r\n\r\nДенис Катаев\r\n\r\nменеджер\r\n\r\nВсеРоботы.ру', '2012-12-02 19:07:13', '2012-12-02 22:36:26', 0, 0, 1),
(3, 1, 'Открытый', 0, 14, 'F70AC98', 'RE: помогите определиться с выбором', 'еще есть вопрос по моющим пылесосам,нужна ежедневная влажная уборка (т.к в семье ребенок-аллергик).\r\n Справиться irobot 230  с 3-хкомнатной квартирой? ковров нет. \r\n Соберет ли хоть какой-то мусор или он только моет?\r\n \r\nИли все таки нужен irobot 390?(но он дорогой) Может существуют аналоги irobota 390? \r\n', '2012-12-02 22:24:55', '2012-12-02 22:24:55', 0, 0, 1),
(4, 1, 'Открытый', 0, 0, 'F70AC98', 'RE: помогите определиться с выбором', 'iRobot 230 небольшой пылесос и предназначен для уборки ванной комнаты и туалета, с большой квартирой ему не справится. В вашем случае действительно нужен iRobot Scooba 385 или iRobot Scooba 390. Эти модели собирают мусор и моют полы, но за ними нужен бОльший уход чем за обычными роботами пылесосами. Более дешевых аналогов им не существует. \r\nВ Clever&amp;Clean 112 есть кстати функция протирки полов с помощью влажной микрофибры. Возможно сейчас стоит взять попробовать недорогой Clever&amp;Clean, если его функций будет недостаточно присмотреться к моющим пылесосам. \r\n\r\nДенис Катаев\r\n\r\nменеджер\r\n\r\nВсеРоботы.ру', '2012-12-02 22:36:26', '2012-12-02 22:36:26', 0, 0, 1);
