INSERT INTO `review` (`review_id`, `product_id`, `customer_id`, `author`, `text`, `rating`, `status`, `date_added`, `date_modified`) VALUES
(1, 51, 0, 'Денис', 'Пылесос отлично убирает шерсть животных.', 5, 1, '2012-07-07 12:46:53', '0000-00-00 00:00:00'),
(4, 52, 0, 'Алина', 'При разработке данного пылесоса, за основу бралось самое лучшее из более старых представителей данной модели, а также  современные инновационные технологии. Чудо-робот действительно на высоком уровне осуществляет уборку,  самостоятельно находя загрязнённые участки. Даже отсутствие мусорного контейнера не делает пылесос менее востребованным. ', 5, 1, '2012-12-01 12:13:40', '0000-00-00 00:00:00'),
(5, 98, 0, 'Саша К.', 'Пожалуй лучшая модель из всей  700-й линейки. Пылесос оснащён тремя координаторами, благодаря которым появляется возможность убирать довольно большие площади. Кроме того, робот без проблем  собирает как шерсть животных, так и пыль. Влажную уборку после него можно делать значительно реже. \r\n', 5, 1, '2012-12-02 01:44:54', '0000-00-00 00:00:00'),
(6, 92, 0, 'Егор ', 'Данная модель может стать отличным подарком как для жены, так и мамы. Надо сказать, что подобный робот способен на довольно высоком уровне осуществлять уборку, а в частности собирать мусор. При этом работает он очень тихо. Кроме того, нигде не застревает  и достаточно резво преодолевает  различные препятствия. Надо сказать, что подобный пылесос стоит достаточно немало, однако недостатком это назвать можно с большой натяжкой.', 5, 1, '2012-12-02 01:46:04', '0000-00-00 00:00:00'),
(7, 51, 0, 'Алла', 'Подобный чудо-пылесос  способен быстро и при этом достаточно качественно произвести уборку в вашем доме. А очистка контейнера для сбора мусора весьма удобна и не займёт у вас много времени. Умный пылесос работает практически бесшумно и в силах самостоятельно доехать до базы. Однако проблему  представляют проводу, которые нужно прибрать.  Цена агрегата может порадовать далеко не каждого, однако качество того стоит.', 5, 1, '2012-12-02 01:47:55', '0000-00-00 00:00:00'),
(8, 150, 0, 'Кирилл', 'Удобный агрегат для качественной уборки, однако стоимость, мягко говоря немаленькая. \r\n Пылесос оснащён довольно широким функционалом с возможностью различного программирования, а также достаточно удобный контейнером. Кроме того, обладает мощным мотором, при этом работает практически бесшумно.\r\nСтоит отметить, что влажную уборку данная модель делает не очень качественно и в режиме  авто довольно плохо ориентируется.\r\n', 4, 1, '2012-12-02 01:48:44', '0000-00-00 00:00:00'),
(9, 57, 0, 'Андрей Л.', 'Отличная и качественная модель для осуществления в доме влажной уборки. Для мытья, агрегат использует только чистую воду, а уже использованную он сливает в специально предназначенный резервуар. Процесс уборки занимает минимальное количество времени, а также усилий с вашей стороны. Однако стоит отметить, что данная  модель не заменяет пылесос, и к тому же достаточно шумно работает.', 4, 1, '2012-12-02 01:50:20', '0000-00-00 00:00:00'),
(10, 56, 0, 'Мария', 'Отличная и качественная модель, которая сможет помочь вам в уборке по дому.  В доме с подобным агрегатом чисто будет  всегда и вам даже не придётся пользоваться веником. Робот достаточно прост в управлении, имеет довольно широкий функционал и при этом работает практически бесшумно. Но следует отметить, что пылесос неравнодушен к проводам – перед уборкой их нужно убирать, кроме того он может застревать под мебелью.', 5, 1, '2012-12-02 01:51:01', '0000-00-00 00:00:00'),
(11, 74, 0, 'Филипп', 'Подобная модель может стать отличным помощником  для уборки, в которой вам даже не придется участвовать. Достаточно лишь запрограммировать робота и он выполнит всю работу самостоятельно.  Легко программируется, имеет функцию самоочищения, кроме того, практически никогда не застревает', 5, 1, '2012-12-02 01:51:44', '0000-00-00 00:00:00'),
(12, 87, 0, 'Юрий', 'Хороший и довольно качественный агрегат, который способен помочь вам уборкой квартиры.  Работает  совершенно на любом покрытия. Подобное изделие оснащено достаточно широким функционалом, который довольно удобен в управлении.', 5, 1, '2012-12-02 01:52:39', '0000-00-00 00:00:00'),
(13, 80, 0, 'Дамир К.', 'Данная модель имеет достаточно качественные щётки,  благодаря которым уборка выполняется на высоком уровне. Кроме того, агрегат имеет широкий функционал, а также большой контейнер для мусора. Однако надо сказать, что как и другие модели, он имеет привычку застревать под мебелью.', 4, 1, '2012-12-02 01:53:59', '0000-00-00 00:00:00'),
(15, 52, 0, 'Вова', 'Прежде чем купить этот пылесос очень много лазил по интернету. В итоге остановился на этой модели и iclebo arte. Вообщем оказался весьма доволен покупкой. Модель, конечно более дорогая чем арте, и, возможно, в больше степени из-за бренда, но потраченных денег я не жалею. пылесос работает исправно, мою квартиру в 140 м2 убирает сам без моего участия.', 5, 1, '2012-12-08 21:51:11', '0000-00-00 00:00:00'),
(16, 92, 0, 'Андрей', 'Отличная модель, приобретением доволен. Дома с успехом убирает залу и коридор от шерсти собаки(и там и там ламинат). Вообщем, не смотря на высокую цену, пылесосом доволен, нареканий по качеству или работе нет.', 5, 1, '2012-12-08 21:52:20', '0000-00-00 00:00:00'),
(17, 98, 0, 'Леша', 'Что сказать топовая модель irobot, айфон среди пылесосов) Аппаратом доволен, когда покупал рассчитывал на меньшее. Прекрасно убирает шерсть,пыль, мелкий мусор с паркета. Плюс кошка очень довольна, все время за ним носится.', 5, 1, '2012-12-08 21:52:07', '0000-00-00 00:00:00'),
(18, 57, 0, 'Денис', 'Интересный пылесос, данная модель отличается от большинства других тем, что он моющий. Покупал его для того, чтобы он в мое отсутствие протирал пол по всей квартире, с этой задачей аппарат справляется. В итоге дома всегда свежо. Как писал автор выше пылесос немного шумноват, но лично меня это не особо напрягает.', 5, 1, '2012-12-08 21:51:48', '0000-00-00 00:00:00'),
(19, 92, 0, 'Иван', 'Отличный аппарат за свою цену. По функционалу ничем не уступает более дорогим irobot 780. С плюсов этого аппарата можно назвать долгое время работы от аккумулятора, хорошо ищет базу, может преодолевать небольшие препятствия. Из недостатков он немного шумноват, но думаю это проблема всех пылесосов.', 5, 1, '2012-12-08 21:51:27', '0000-00-00 00:00:00'),
(21, 157, 0, 'Анастасия', 'Всегда находит базу, уверено перемещается по квартире,грязи после себя не оставляет, сложные места убирает дважды. \r\nНравится что можно задать время уборки (у меня убирает раз в два дня днем, когда я на работе). \r\nОчень довольна приобретением!\r\n', 5, 1, '2013-07-14 19:13:02', '0000-00-00 00:00:00'),
(22, 156, 0, 'Сергей', 'Купил сегодня iclebo arte в подарок. Магазин классный,доставили быстро (четко в обговоренное время) во всем идут навстречу клиенту. ', 5, 1, '2013-07-14 19:12:51', '0000-00-00 00:00:00'),
(23, 156, 0, 'Миша', 'Благодарю Vseroboty за помощь в выборе домашнего помощника! \r\nПользуюсь им чуть меньше месяца. В целом очень нравится, экономит время, не шумный? способен убрать там, где обычным пылесосом не подлезешь...  Из недостатков - иногда застревает на коврах.\r\nНо в целом приобретением доволен.', 5, 1, '2013-07-14 19:12:41', '0000-00-00 00:00:00');
